import React from "react";

const SalaryInsights = ({ insights }) => {
  if (!insights) return null;

  const formatSalary = (amount) => {
    return new Intl.NumberFormat("en-IN", {
      style: "currency",
      currency: "INR",
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatUSSalary = (amount) => {
    return new Intl.NumberFormat("en-US", {
      style: "currency",
      currency: "USD",
      maximumFractionDigits: 0,
    }).format(amount);
  };

  return (
    <div className="bg-white rounded-lg shadow-lg p-6">
      <h2 className="text-2xl font-semibold text-gray-800 mb-6">
        Salary Insights
      </h2>

      {/* US Salary */}
      <div className="mb-6">
        <h3 className="text-lg font-medium text-indigo-600 mb-3">US Market</h3>
        <div className="bg-blue-50 p-4 rounded-lg">
          <p className="text-xl font-semibold text-blue-800">
            Average Salary: {formatUSSalary(insights.US_Avg_Salary)}
          </p>
        </div>
      </div>

      {/* India Salary */}
      <div className="mb-6">
        <h3 className="text-lg font-medium text-indigo-600 mb-3">
          Indian Market
        </h3>
        <div className="bg-green-50 p-4 rounded-lg">
          <p className="text-xl font-semibold text-green-800 mb-4">
            Average Salary: {formatSalary(insights.India_Avg_Salary)}
          </p>

          <div className="space-y-3">
            <div className="flex justify-between items-center">
              <span className="text-gray-600">Entry Level (0-2 years)</span>
              <span className="font-semibold text-green-700">
                {formatSalary(insights.Entry_Level_Salary_India)}
              </span>
            </div>
            <div className="flex justify-between items-center">
              <span className="text-gray-600">Mid Level (3-5 years)</span>
              <span className="font-semibold text-green-700">
                {formatSalary(insights.Mid_Level_Salary_India)}
              </span>
            </div>
            <div className="flex justify-between items-center">
              <span className="text-gray-600">Senior Level (6+ years)</span>
              <span className="font-semibold text-green-700">
                {formatSalary(insights.High_Level_Salary_India)}
              </span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SalaryInsights;
