import React, { useState } from "react";
import axios from "axios";

const ResumeBuilder = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    location: "",
    summary: "",
    experience: "",
    education: "",
    skills: "",
    linkedin: "",
    github: "",
    portfolio: "",
    languages: "",
    certifications: "",
    awards: "",
    projects: "",
    publications: "",
    volunteer_work: "",
    interests: "",
    references: "",
    professional_affiliations: "",
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [resume, setResume] = useState(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setResume(null);

    try {
      // Format experience data
      const formattedExperience = formData.experience
        .split("\n\n")
        .filter((exp) => exp.trim())
        .map((exp) => {
          const lines = exp.split("\n");
          return {
            company: lines[0] || "",
            position: lines[1] || "",
            dates: lines[2] || "",
            description: lines.slice(3).join("\n") || "",
          };
        });

      // Format education data
      const formattedEducation = formData.education
        .split("\n\n")
        .filter((edu) => edu.trim())
        .map((edu) => {
          const lines = edu.split("\n");
          return {
            school: lines[0] || "",
            degree: lines[1] || "",
            dates: lines[2] || "",
            description: lines.slice(3).join("\n") || "",
          };
        });

      const response = await fetch("http://localhost:8000/generate-resume", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          name: formData.name,
          email: formData.email,
          phone: formData.phone,
          location: formData.location,
          summary: formData.summary,
          experience: formattedExperience,
          education: formattedEducation,
          skills: formData.skills,
          linkedin: formData.linkedin || null,
          github: formData.github || null,
          portfolio: formData.portfolio || null,
          languages: formData.languages || null,
          certifications: formData.certifications || null,
          awards: formData.awards || null,
          projects: formData.projects || null,
          publications: formData.publications || null,
          volunteer_work: formData.volunteer_work || null,
          interests: formData.interests || null,
          references: formData.references || null,
          professional_affiliations: formData.professional_affiliations || null,
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || "Failed to generate resume");
      }

      const data = await response.json();
      setResume(data);
    } catch (err) {
      console.error("Error generating resume:", err);
      setError(err.message || "Error generating resume. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const downloadResume = () => {
    if (!resume) return;

    // Convert base64 to blob
    const byteCharacters = atob(resume.content);
    const byteNumbers = new Array(byteCharacters.length);
    for (let i = 0; i < byteCharacters.length; i++) {
      byteNumbers[i] = byteCharacters.charCodeAt(i);
    }
    const byteArray = new Uint8Array(byteNumbers);
    const blob = new Blob([byteArray], { type: "application/pdf" });

    // Create download link
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "resume.pdf";
    document.body.appendChild(a);
    a.click();

    // Cleanup
    window.URL.revokeObjectURL(url);
    document.body.removeChild(a);
  };

  return (
    <div className="space-y-6">
      <div className="bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-2xl font-semibold text-gray-800 mb-6">
          Create Your ATS-Friendly Resume
        </h2>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Name
              </label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Email
              </label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Phone
              </label>
              <input
                type="tel"
                name="phone"
                value={formData.phone}
                onChange={handleInputChange}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Location
              </label>
              <input
                type="text"
                name="location"
                value={formData.location}
                onChange={handleInputChange}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                required
              />
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">
              Professional Summary
            </label>
            <textarea
              name="summary"
              value={formData.summary}
              onChange={handleInputChange}
              rows={3}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
              placeholder="Write a brief summary of your professional background and career objectives..."
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">
              Work Experience
            </label>
            <textarea
              name="experience"
              value={formData.experience}
              onChange={handleInputChange}
              rows={4}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
              placeholder="Enter your work experience in chronological order. For each position, include:&#10;Company Name&#10;Position&#10;Duration&#10;Key responsibilities and achievements"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">
              Education
            </label>
            <textarea
              name="education"
              value={formData.education}
              onChange={handleInputChange}
              rows={3}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
              placeholder="Enter your educational background. For each degree, include:&#10;Institution&#10;Degree&#10;Graduation Year&#10;Relevant coursework or achievements"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">
              Skills
            </label>
            <textarea
              name="skills"
              value={formData.skills}
              onChange={handleInputChange}
              rows={3}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
              placeholder="Enter your skills, separated by commas. Include technical skills, soft skills, and any relevant certifications..."
              required
            />
          </div>

          {/* Optional Fields */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700">
                LinkedIn Profile
              </label>
              <input
                type="url"
                name="linkedin"
                value={formData.linkedin}
                onChange={handleInputChange}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                placeholder="https://linkedin.com/in/yourprofile"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">
                GitHub Profile
              </label>
              <input
                type="url"
                name="github"
                value={formData.github}
                onChange={handleInputChange}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                placeholder="https://github.com/yourusername"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Portfolio Website
              </label>
              <input
                type="url"
                name="portfolio"
                value={formData.portfolio}
                onChange={handleInputChange}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                placeholder="https://yourportfolio.com"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Languages
              </label>
              <input
                type="text"
                name="languages"
                value={formData.languages}
                onChange={handleInputChange}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                placeholder="English, Spanish, etc."
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Certifications
              </label>
              <textarea
                name="certifications"
                value={formData.certifications}
                onChange={handleInputChange}
                rows={2}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                placeholder="List your certifications..."
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Awards & Achievements
              </label>
              <textarea
                name="awards"
                value={formData.awards}
                onChange={handleInputChange}
                rows={2}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                placeholder="List your awards and achievements..."
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Projects
              </label>
              <textarea
                name="projects"
                value={formData.projects}
                onChange={handleInputChange}
                rows={2}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                placeholder="List your notable projects..."
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Publications
              </label>
              <textarea
                name="publications"
                value={formData.publications}
                onChange={handleInputChange}
                rows={2}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                placeholder="List your publications..."
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Volunteer Work
              </label>
              <textarea
                name="volunteer_work"
                value={formData.volunteer_work}
                onChange={handleInputChange}
                rows={2}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                placeholder="List your volunteer experience..."
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Interests
              </label>
              <input
                type="text"
                name="interests"
                value={formData.interests}
                onChange={handleInputChange}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                placeholder="Your professional interests..."
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">
                References
              </label>
              <textarea
                name="references"
                value={formData.references}
                onChange={handleInputChange}
                rows={2}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                placeholder="List your references..."
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Professional Affiliations
              </label>
              <textarea
                name="professional_affiliations"
                value={formData.professional_affiliations}
                onChange={handleInputChange}
                rows={2}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                placeholder="List your professional memberships..."
              />
            </div>
          </div>

          <button
            type="submit"
            disabled={loading}
            className="w-full py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {loading ? "Generating Resume..." : "Generate Resume"}
          </button>
        </form>

        {/* Error Message */}
        {error && (
          <div className="mt-4 bg-red-50 text-red-700 p-4 rounded-lg">
            {error}
          </div>
        )}

        {/* Resume Preview */}
        {resume && (
          <div className="mt-6">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-lg font-medium text-gray-900">
                Your ATS-Friendly Resume
              </h3>
              <button
                onClick={downloadResume}
                className="px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
              >
                Download PDF
              </button>
            </div>
            <div className="bg-white p-6 rounded-lg shadow-md">
              <div
                className="prose max-w-none"
                dangerouslySetInnerHTML={{ __html: resume.preview }}
              />
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ResumeBuilder;
