import React, { useState } from "react";

const InputSection = ({ onPredict, loading }) => {
  const [skills, setSkills] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (skills.trim()) {
      onPredict(skills.trim());
    }
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md mb-6">
      <h2 className="text-xl font-semibold mb-4">Enter Your Skills</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label
            htmlFor="skills"
            className="block text-sm font-medium text-gray-700 mb-1"
          >
            List your skills (comma-separated)
          </label>
          <textarea
            id="skills"
            value={skills}
            onChange={(e) => setSkills(e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
            rows={4}
            placeholder="e.g., Python, JavaScript, Data Analysis, Machine Learning"
            required
            disabled={loading}
          />
        </div>
        <button
          type="submit"
          disabled={loading || !skills.trim()}
          className={`w-full py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 ${
            loading || !skills.trim() ? "opacity-50 cursor-not-allowed" : ""
          }`}
        >
          {loading ? "Predicting..." : "Predict Career"}
        </button>
      </form>
    </div>
  );
};

export default InputSection;
