import React, { useState } from "react";
import axios from "axios";
import Hero from "./sections/Hero";
import PersonalInfo from "./sections/PersonalInfo";
import ProfessionalSummary from "./sections/ProfessionalSummary";
import Experience from "./sections/Experience";
import Education from "./sections/Education";
import Skills from "./sections/Skills";
import AdditionalInfo from "./sections/AdditionalInfo";
import Button from "./ui/Button";
import Card from "./ui/Card";

const ResumeBuilder = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    location: "",
    linkedin: "",
    github: "",
    portfolio: "",
    summary: "",
    experience: [],
    education: [],
    skills: "",
    certifications: "",
    awards: "",
    projects: "",
    publications: "",
    volunteer_work: "",
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.includes(".")) {
      const [parent, index, field] = name.split(".");
      setFormData((prev) => ({
        ...prev,
        [parent]: prev[parent].map((item, i) =>
          i === parseInt(index) ? { ...item, [field]: value } : item
        ),
      }));
    } else {
      setFormData((prev) => ({
        ...prev,
        [name]: value,
      }));
    }
  };

  const handleAddExperience = () => {
    setFormData((prev) => ({
      ...prev,
      experience: [
        ...prev.experience,
        {
          company: "",
          position: "",
          start_date: "",
          end_date: "",
          description: "",
        },
      ],
    }));
  };

  const handleRemoveExperience = (index) => {
    setFormData((prev) => ({
      ...prev,
      experience: prev.experience.filter((_, i) => i !== index),
    }));
  };

  const handleAddEducation = () => {
    setFormData((prev) => ({
      ...prev,
      education: [
        ...prev.education,
        {
          school: "",
          degree: "",
          start_date: "",
          end_date: "",
          description: "",
        },
      ],
    }));
  };

  const handleRemoveEducation = (index) => {
    setFormData((prev) => ({
      ...prev,
      education: prev.education.filter((_, i) => i !== index),
    }));
  };

  const calculateProgress = () => {
    const requiredFields = [
      "name",
      "email",
      "phone",
      "location",
      "summary",
      "skills",
    ];
    const filledFields = requiredFields.filter(
      (field) => formData[field].trim() !== ""
    );
    return Math.round((filledFields.length / requiredFields.length) * 100);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setSuccess(false);

    try {
      const response = await axios.post(
        "http://localhost:8000/generate-resume",
        formData,
        {
          responseType: "blob",
          headers: {
            "Content-Type": "application/json",
            Accept: "application/pdf",
          },
        }
      );

      const blob = new Blob([response.data], { type: "application/pdf" });
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = "resume.pdf";
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);

      setSuccess(true);
    } catch (err) {
      console.error("Error generating resume:", err);
      if (err.response) {
        // The request was made and the server responded with a status code
        // that falls out of the range of 2xx
        setError(
          err.response.data?.detail ||
            "Failed to generate resume. Please try again."
        );
      } else if (err.request) {
        // The request was made but no response was received
        setError(
          "No response from server. Please check if the backend is running."
        );
      } else {
        // Something happened in setting up the request that triggered an Error
        setError("Error setting up the request. Please try again.");
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Hero />

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        <form onSubmit={handleSubmit} className="space-y-8">
          <PersonalInfo
            formData={formData}
            handleChange={handleChange}
            progress={calculateProgress()}
          />

          <ProfessionalSummary
            formData={formData}
            handleChange={handleChange}
          />

          <Experience
            formData={formData}
            handleChange={handleChange}
            handleAddExperience={handleAddExperience}
            handleRemoveExperience={handleRemoveExperience}
          />

          <Education
            formData={formData}
            handleChange={handleChange}
            handleAddEducation={handleAddEducation}
            handleRemoveEducation={handleRemoveEducation}
          />

          <Skills formData={formData} handleChange={handleChange} />

          <AdditionalInfo formData={formData} handleChange={handleChange} />

          {error && (
            <Card className="bg-red-50 border-red-200">
              <p className="text-red-600">{error}</p>
            </Card>
          )}

          {success && (
            <Card className="bg-green-50 border-green-200">
              <p className="text-green-600">Resume generated successfully!</p>
            </Card>
          )}

          <div className="flex justify-end">
            <Button
              type="submit"
              disabled={loading}
              className="w-full sm:w-auto"
            >
              {loading ? "Generating..." : "Generate Resume"}
            </Button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ResumeBuilder;
