import React from "react";
import { motion } from "framer-motion";
import { Link } from "react-router-dom";

const HomePage = () => {
  const features = [
    {
      title: "Career Prediction",
      description:
        "Get personalized career recommendations based on your skills and interests.",
      icon: "üéØ",
      link: "/career-predictor",
    },
    {
      title: "Resume Builder",
      description: "Create professional resumes with our easy-to-use builder.",
      icon: "üìù",
      link: "/resume-tools",
    },
    {
      title: "Career Insights",
      description:
        "Get detailed information about salary ranges, required skills, and growth opportunities.",
      icon: "üìä",
      link: "/career-predictor",
    },
  ];

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Welcome Section */}
      <section className="h-screen flex items-center justify-center bg-gradient-to-br from-purple-600 to-purple-800 text-white">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className="text-center px-4"
        >
          <h1 className="text-5xl font-bold mb-6">
            Welcome to Career Predictor
          </h1>
          <p className="text-xl mb-8">
            Discover your perfect career path with AI-powered insights
          </p>
          <Link
            to="/career-predictor"
            className="bg-white text-purple-600 px-8 py-3 rounded-full font-semibold hover:bg-purple-50 transition-colors"
          >
            Get Started
          </Link>
        </motion.div>
      </section>

      {/* Features Section */}
      <section className="py-20 px-4">
        <div className="max-w-6xl mx-auto">
          <motion.h2
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
            className="text-3xl font-bold text-center text-gray-800 mb-12"
          >
            Features
          </motion.h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {features.map((feature, index) => (
              <motion.div
                key={feature.title}
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: index * 0.2 }}
                viewport={{ once: true }}
                className="bg-white rounded-lg shadow-md p-6 hover:shadow-lg transition-shadow"
              >
                <div className="text-4xl mb-4">{feature.icon}</div>
                <h3 className="text-xl font-semibold text-gray-800 mb-2">
                  {feature.title}
                </h3>
                <p className="text-gray-600 mb-4">{feature.description}</p>
                <Link
                  to={feature.link}
                  className="text-purple-600 font-medium hover:text-purple-700 transition-colors"
                >
                  Learn more ‚Üí
                </Link>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="py-20 px-4 bg-purple-50">
        <div className="max-w-4xl mx-auto text-center">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            <h2 className="text-3xl font-bold text-gray-800 mb-6">
              Ready to Start Your Career Journey?
            </h2>
            <p className="text-gray-600 mb-8">
              Get personalized career recommendations and insights to help you
              make informed decisions.
            </p>
            <Link
              to="/career-predictor"
              className="bg-purple-600 text-white px-8 py-3 rounded-full font-semibold hover:bg-purple-700 transition-colors"
            >
              Start Now
            </Link>
          </motion.div>
        </div>
      </section>
    </div>
  );
};

export default HomePage;
