import React from "react";

const SalaryInsights = ({ insights }) => {
  if (!insights) return null;

  const formatSalary = (amount) => {
    return new Intl.NumberFormat("en-IN", {
      style: "currency",
      currency: "INR",
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatUSSalary = (amount) => {
    return new Intl.NumberFormat("en-US", {
      style: "currency",
      currency: "USD",
      maximumFractionDigits: 0,
    }).format(amount);
  };

  return (
    <div className="bg-white rounded-lg shadow-md p-6">
      <h2 className="text-2xl font-bold text-purple-600 mb-4">
        Salary Insights
      </h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Salary Range */}
        <div className="bg-purple-50 rounded-lg p-4">
          <h3 className="text-lg font-semibold text-purple-800 mb-2">
            Salary Range
          </h3>
          <div className="space-y-2">
            <div className="flex justify-between items-center">
              <span className="text-gray-600">Entry Level:</span>
              <span className="font-medium text-purple-800">
                {formatSalary(insights.Entry_Level_Salary_India)}
              </span>
            </div>
            <div className="flex justify-between items-center">
              <span className="text-gray-600">Mid Level:</span>
              <span className="font-medium text-purple-800">
                {formatSalary(insights.Mid_Level_Salary_India)}
              </span>
            </div>
            <div className="flex justify-between items-center">
              <span className="text-gray-600">Senior Level:</span>
              <span className="font-medium text-purple-800">
                {formatSalary(insights.High_Level_Salary_India)}
              </span>
            </div>
          </div>
        </div>

        {/* Average Salaries */}
        <div className="bg-purple-50 rounded-lg p-4">
          <h3 className="text-lg font-semibold text-purple-800 mb-2">
            Average Salaries
          </h3>
          <div className="space-y-2">
            <div className="flex justify-between items-center">
              <span className="text-gray-600">US Market:</span>
              <span className="font-medium text-purple-800">
                {formatUSSalary(insights.US_Avg_Salary)}
              </span>
            </div>
            <div className="flex justify-between items-center">
              <span className="text-gray-600">Indian Market:</span>
              <span className="font-medium text-purple-800">
                {formatSalary(insights.India_Avg_Salary)}
              </span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SalaryInsights;
