import React from 'react';

const JobInsights = ({ insights }) => {
  if (!insights) return null;

  const getDemandColor = (demand) => {
    switch (demand.toLowerCase()) {
      case 'high':
        return 'text-green-600';
      case 'moderate':
        return 'text-yellow-600';
      case 'low':
        return 'text-red-600';
      default:
        return 'text-gray-600';
    }
  };

  const getGrowthColor = (growth) => {
    if (growth.toLowerCase().includes('rapidly') || growth.toLowerCase().includes('explosive')) {
      return 'text-green-600';
    } else if (growth.toLowerCase().includes('steady')) {
      return 'text-yellow-600';
    } else {
      return 'text-gray-600';
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-lg p-6">
      <h2 className="text-2xl font-semibold text-gray-800 mb-6">Job Market Insights</h2>
      
      {/* Demand and Growth */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
        <div className="bg-purple-50 p-4 rounded-lg">
          <h3 className="text-lg font-medium text-indigo-600 mb-2">Market Demand</h3>
          <p className={`text-xl font-semibold ${getDemandColor(insights.Demand_in_India)}`}>
            {insights.Demand_in_India}
          </p>
        </div>
        
        <div className="bg-orange-50 p-4 rounded-lg">
          <h3 className="text-lg font-medium text-indigo-600 mb-2">Growth Trend</h3>
          <p className={`text-xl font-semibold ${getGrowthColor(insights.Growth_in_India)}`}>
            {insights.Growth_in_India}
          </p>
        </div>
      </div>

      {/* Top Companies */}
      <div>
        <h3 className="text-lg font-medium text-indigo-600 mb-3">Top Hiring Companies</h3>
        <div className="flex flex-wrap gap-2">
          {insights.Top_Companies.split(', ').map((company, index) => (
            <span
              key={index}
              className="bg-gray-100 text-gray-800 px-4 py-2 rounded-lg text-sm font-medium hover:bg-gray-200 transition-colors"
            >
              {company}
            </span>
          ))}
        </div>
      </div>
    </div>
  );
};

export default JobInsights;
