import React, { useState, useEffect } from "react";
import axios from "axios";

const CAREER_ROLES = [
  "Software Development and Engineering",
  "Artificial Intelligence",
  "Development",
  "Cybersecurity",
  "Data Science",
  "UI/UX Design",
];

const ResumeAnalysis = () => {
  const [file, setFile] = useState(null);
  const [selectedRole, setSelectedRole] = useState("");
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [careerInsights, setCareerInsights] = useState(null);

  useEffect(() => {
    const fetchCareerInsights = async () => {
      try {
        const response = await axios.get("http://localhost:5000/careers");
        setCareerInsights(response.data);
      } catch (err) {
        console.error("Error fetching career insights:", err);
      }
    };
    fetchCareerInsights();
  }, []);

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    // Check if file is PDF
    if (file.type !== "application/pdf") {
      setError("Please upload a PDF file");
      return;
    }

    setFile(file);
    setError(null);
  };

  const handleAnalyze = async () => {
    if (!file || !selectedRole) {
      setError("Please upload a resume and select a career role");
      return;
    }

    setLoading(true);
    setError(null);
    setAnalysis(null);

    const formData = new FormData();
    formData.append("resume", file);

    // Get required skills for the selected role
    const roleInsights = careerInsights?.find(
      (insight) => insight.Career === selectedRole
    );
    if (!roleInsights) {
      setError("Career role not found");
      setLoading(false);
      return;
    }

    formData.append("required_skills", roleInsights["In-Demand Skills"]);

    try {
      const response = await axios.post(
        "http://localhost:5000/analyze-resume",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );
      setAnalysis(response.data);
    } catch (err) {
      setError("Error analyzing resume. Please try again.");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const getMatchPercentage = () => {
    if (!analysis?.matched_skills || !analysis?.required_skills) return 0;
    return Math.round(
      (analysis.matched_skills.length / analysis.required_skills.length) * 100
    );
  };

  return (
    <div className="space-y-6">
      <div className="bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-2xl font-semibold text-gray-800 mb-6">
          Resume Analysis
        </h2>

        {/* Career Role Selection */}
        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Select Your Target Career Role
          </label>
          <select
            value={selectedRole}
            onChange={(e) => setSelectedRole(e.target.value)}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
            required
          >
            <option value="">Choose a career role...</option>
            {CAREER_ROLES.map((role) => (
              <option key={role} value={role}>
                {role}
              </option>
            ))}
          </select>
        </div>

        {/* Resume Upload */}
        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Upload your resume (PDF only)
          </label>
          <input
            type="file"
            accept=".pdf"
            onChange={handleFileUpload}
            className="block w-full text-sm text-gray-500
              file:mr-4 file:py-2 file:px-4
              file:rounded-full file:border-0
              file:text-sm file:font-semibold
              file:bg-indigo-50 file:text-indigo-700
              hover:file:bg-indigo-100"
          />
        </div>

        {/* Analyze Button */}
        <button
          onClick={handleAnalyze}
          disabled={loading || !file || !selectedRole}
          className="w-full py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50 disabled:cursor-not-allowed"
        >
          {loading ? "Analyzing..." : "Analyze Resume"}
        </button>

        {/* Error Message */}
        {error && (
          <div className="mt-4 bg-red-50 text-red-700 p-4 rounded-lg">
            {error}
          </div>
        )}

        {/* Analysis Results */}
        {analysis && (
          <div className="mt-6 space-y-4">
            <div className="bg-indigo-50 p-4 rounded-lg">
              <h3 className="text-lg font-medium text-indigo-800 mb-2">
                Match Score
              </h3>
              <div className="flex items-center">
                <div className="text-3xl font-bold text-indigo-600">
                  {getMatchPercentage()}%
                </div>
                <div className="ml-2 text-gray-600">
                  match with required skills for {selectedRole}
                </div>
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {/* Matched Skills */}
              <div className="bg-green-50 p-4 rounded-lg">
                <h3 className="text-lg font-medium text-green-800 mb-2">
                  Matched Skills
                </h3>
                <ul className="list-disc list-inside space-y-1">
                  {analysis.matched_skills.map((skill, index) => (
                    <li key={index} className="text-green-700">
                      {skill}
                    </li>
                  ))}
                </ul>
              </div>

              {/* Missing Skills */}
              <div className="bg-yellow-50 p-4 rounded-lg">
                <h3 className="text-lg font-medium text-yellow-800 mb-2">
                  Skills to Develop
                </h3>
                <ul className="list-disc list-inside space-y-1">
                  {analysis.missing_skills.map((skill, index) => (
                    <li key={index} className="text-yellow-700">
                      {skill}
                    </li>
                  ))}
                </ul>
              </div>
            </div>

            {/* Recommendations */}
            <div className="bg-blue-50 p-4 rounded-lg">
              <h3 className="text-lg font-medium text-blue-800 mb-2">
                Recommendations
              </h3>
              <ul className="list-disc list-inside space-y-1">
                {analysis.recommendations.map((rec, index) => (
                  <li key={index} className="text-blue-700">
                    {rec}
                  </li>
                ))}
              </ul>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ResumeAnalysis;
