import React, { useEffect, useState } from "react";
import { fetchCareerData } from "./api";
import InputSection from "./components/InputSection";
import PredictedCareer from "./components/PredictedCareer";
import SalaryInsights from "./components/SalaryInsights";
import JobInsights from "./components/JobInsights";
import CareerRoadmap from "./components/CareerRoadmap";

function App() {
  const [data, setData] = useState(null);
  const [selectedSkills, setSelectedSkills] = useState([]);
  const [selectedField, setSelectedField] = useState("");
  const [predictedCareer, setPredictedCareer] = useState(null);

  useEffect(() => {
    const getData = async () => {
      const result = await fetchCareerData();
      setData(result);
    };
    getData();
  }, []);

  // Function to predict career
  const handlePredict = (skill, field) => {
    console.log("Predicting career for:", skill, field);
    // Mock career prediction (replace with actual API call later)
    setPredictedCareer("Software Engineer"); // Example career
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-2xl font-bold text-center mb-6">Career Predictor</h1>

      {/* Input Section with onPredict function */}
      <InputSection
        selectedSkills={selectedSkills}
        setSelectedSkills={setSelectedSkills}
        selectedField={selectedField}
        setSelectedField={setSelectedField}
        onPredict={handlePredict} // ✅ Pass the function here
      />

      {/* Predicted Career */}
      {predictedCareer && <PredictedCareer career={predictedCareer} />}

      {/* Career Insights */}
      {data && predictedCareer && (
        <>
          <SalaryInsights career={predictedCareer} data={data} />
          <JobInsights career={predictedCareer} data={data} />
          <CareerRoadmap career={predictedCareer} data={data} />
        </>
      )}
    </div>
  );
}
export default App;
