import React from "react";

const COMPANY_LOGOS = {
  Infosys:
    "https://upload.wikimedia.org/wikipedia/commons/9/95/Infosys_logo.svg",
  Wipro: "https://upload.wikimedia.org/wikipedia/commons/1/1c/Wipro_Logo.svg",
  Cognizant:
    "https://upload.wikimedia.org/wikipedia/commons/4/4b/Cognizant_logo_2022.svg",
  ThoughtWorks:
    "https://upload.wikimedia.org/wikipedia/commons/9/9c/ThoughtWorks_logo.svg",
  Zoho: "https://upload.wikimedia.org/wikipedia/commons/4/4c/Zoho_Corporation_logo.svg",
  Razorpay:
    "https://upload.wikimedia.org/wikipedia/commons/3/3c/Razorpay_logo.svg",
  Freshworks:
    "https://upload.wikimedia.org/wikipedia/commons/2/2c/Freshworks_logo.svg",
  "Palo Alto Networks":
    "https://upload.wikimedia.org/wikipedia/commons/3/3c/Palo_Alto_Networks_logo.svg",
  Cisco: "https://upload.wikimedia.org/wikipedia/commons/0/08/Cisco_logo.svg",
  IBM: "https://upload.wikimedia.org/wikipedia/commons/5/51/IBM_logo.svg",
  "Check Point":
    "https://upload.wikimedia.org/wikipedia/commons/4/4c/Check_Point_Software_Technologies_logo.svg",
  Kaspersky:
    "https://upload.wikimedia.org/wikipedia/commons/2/2c/Kaspersky_Lab_logo.svg",
  Deloitte:
    "https://upload.wikimedia.org/wikipedia/commons/1/15/Deloitte_Logo.svg",
  EY: "https://upload.wikimedia.org/wikipedia/commons/2/2c/EY_logo.svg",
  Accenture:
    "https://upload.wikimedia.org/wikipedia/commons/c/cd/Accenture.svg",
  Microsoft:
    "https://upload.wikimedia.org/wikipedia/commons/4/44/Microsoft_logo.svg",
  Google:
    "https://upload.wikimedia.org/wikipedia/commons/2/2f/Google_2015_logo.svg",
  Amazon: "https://upload.wikimedia.org/wikipedia/commons/a/a9/Amazon_logo.svg",
  Meta: "https://upload.wikimedia.org/wikipedia/commons/7/7b/Meta_Platforms_Inc._logo.svg",
  Apple:
    "https://upload.wikimedia.org/wikipedia/commons/f/fa/Apple_logo_black.svg",
};

const JobInsights = ({ insights }) => {
  if (!insights) return null;

  const getDemandColor = (demand) => {
    switch (demand.toLowerCase()) {
      case "high":
        return "text-green-600";
      case "moderate":
        return "text-yellow-600";
      case "low":
        return "text-red-600";
      default:
        return "text-gray-600";
    }
  };

  const getGrowthColor = (growth) => {
    if (
      growth.toLowerCase().includes("rapidly") ||
      growth.toLowerCase().includes("explosive")
    ) {
      return "text-green-600";
    } else if (growth.toLowerCase().includes("steady")) {
      return "text-yellow-600";
    } else {
      return "text-gray-600";
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-md p-6">
      <h2 className="text-2xl font-bold text-purple-600 mb-4">Job Insights</h2>

      {/* Demand and Growth */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
        <div className="bg-purple-50 p-4 rounded-lg">
          <h3 className="text-lg font-medium text-purple-800 mb-2">
            Market Demand
          </h3>
          <p
            className={`text-xl font-semibold ${getDemandColor(
              insights.Demand_in_India
            )}`}
          >
            {insights.Demand_in_India}
          </p>
        </div>

        <div className="bg-purple-50 p-4 rounded-lg">
          <h3 className="text-lg font-medium text-purple-800 mb-2">
            Growth Trend
          </h3>
          <p
            className={`text-xl font-semibold ${getGrowthColor(
              insights.Growth_in_India
            )}`}
          >
            {insights.Growth_in_India}
          </p>
        </div>
      </div>

      {/* Top Companies */}
      <div>
        <h3 className="text-lg font-medium text-purple-800 mb-3">
          Top Hiring Companies
        </h3>
        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-4">
          {insights.Top_Companies.split(", ").map((company, index) => (
            <div
              key={index}
              className="bg-white border border-gray-200 rounded-lg p-3 flex items-center justify-center hover:shadow-md transition-shadow"
            >
              {COMPANY_LOGOS[company] ? (
                <img
                  src={COMPANY_LOGOS[company]}
                  alt={`${company} logo`}
                  className="h-8 w-auto object-contain"
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src =
                      "https://via.placeholder.com/100x30?text=" + company;
                  }}
                />
              ) : (
                <span className="text-sm font-medium text-gray-700">
                  {company}
                </span>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default JobInsights;
