import { useState } from "react";

const InputSection = ({ onPredict }) => {
  const [selectedSkill, setSelectedSkill] = useState("");
  const [selectedField, setSelectedField] = useState("");

  const skills = [
    "Python",
    "JavaScript",
    "Machine Learning",
    "UI/UX",
    "Cybersecurity",
  ];
  const fields = [
    "Software Development",
    "AI",
    "Data Science",
    "UI/UX",
    "Cybersecurity",
  ];

  const handlePredict = () => {
    if (selectedSkill && selectedField) {
      onPredict(selectedSkill, selectedField); // ✅ Use the function
    } else {
      console.error("Skill or field not selected!");
    }
  };

  return (
    <div className="bg-white p-6 shadow-md rounded-md w-full max-w-lg mx-auto">
      <h2 className="text-xl font-bold mb-4">Select Your Skills & Interest</h2>

      <label className="block mb-2">Select a Skill:</label>
      <select
        className="border p-2 rounded w-full mb-4"
        value={selectedSkill}
        onChange={(e) => setSelectedSkill(e.target.value)}
      >
        <option value="">Choose a skill</option>
        {skills.map((skill, index) => (
          <option key={index} value={skill}>
            {skill}
          </option>
        ))}
      </select>

      <label className="block mb-2">Select an Interested Field:</label>
      <select
        className="border p-2 rounded w-full mb-4"
        value={selectedField}
        onChange={(e) => setSelectedField(e.target.value)}
      >
        <option value="">Choose a field</option>
        {fields.map((field, index) => (
          <option key={index} value={field}>
            {field}
          </option>
        ))}
      </select>

      <button
        onClick={handlePredict}
        className="bg-blue-500 text-white p-2 rounded w-full mt-4 hover:bg-blue-600"
      >
        Predict Career
      </button>
    </div>
  );
};

export default InputSection;
