import React, { useEffect, useState } from "react";
import { fetchCareerData } from "./api";
import InputSection from "./components/InputSection";
import PredictedCareer from "./components/PredictedCareer";
import SalaryInsights from "./components/SalaryInsights";
import JobInsights from "./components/JobInsights";
import CareerRoadmap from "./components/CareerRoadmap";
import axios from "axios";

function App() {
  const [data, setData] = useState(null);
  const [predictedCareer, setPredictedCareer] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getData = async () => {
      const result = await fetchCareerData();
      setData(result);
    };
    getData();
  }, []);

  const handlePredict = async (skills) => {
    try {
      setLoading(true);
      setError(null);
      setPredictedCareer(null);

      const response = await axios.post('http://localhost:5000/predict', {
        skills: skills
      });

      setPredictedCareer(response.data.prediction);
    } catch (err) {
      setError('Error predicting career. Please try again.');
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-2xl font-bold text-center mb-6">Career Predictor</h1>

      {/* Input Section */}
      <InputSection onPredict={handlePredict} loading={loading} />

      {/* Error Message */}
      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-4" role="alert">
          <span className="block sm:inline">{error}</span>
        </div>
      )}

      {/* Predicted Career */}
      {predictedCareer && <PredictedCareer career={predictedCareer} />}

      {/* Career Insights */}
      {data && predictedCareer && (
        <>
          <SalaryInsights career={predictedCareer} data={data} />
          <JobInsights career={predictedCareer} data={data} />
          <CareerRoadmap career={predictedCareer} data={data} />
        </>
      )}
    </div>
  );
}

export default App;
