import React, { useEffect, useState } from "react";
import InputSection from "./components/InputSection";
import PredictedCareer from "./components/PredictedCareer";
import SalaryInsights from "./components/SalaryInsights";
import JobInsights from "./components/JobInsights";
import CareerRoadmap from "./components/CareerRoadmap";
import axios from "axios";

function App() {
  const [careerData, setCareerData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handlePredict = async (skills) => {
    try {
      setLoading(true);
      setError(null);
      setCareerData(null);

      console.log("Sending skills to backend:", skills);
      const response = await axios.post("http://localhost:5000/predict", {
        skills: skills,
      });
      console.log("Received response:", response.data);

      if (response.data && response.data.insights) {
        setCareerData(response.data);
      } else {
        setError("No career insights found for the prediction.");
        console.error("Missing insights in response:", response.data);
      }
    } catch (err) {
      setError("Error predicting career. Please try again.");
      console.error("Error:", err);
    } finally {
      setLoading(false);
    }
  };

  // Debug log when careerData changes
  useEffect(() => {
    console.log("Current careerData:", careerData);
  }, [careerData]);

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-3xl font-bold text-center mb-8 text-indigo-600">
        Career Predictor
      </h1>

      {/* Input Section */}
      <InputSection onPredict={handlePredict} loading={loading} />

      {/* Error Message */}
      {error && (
        <div
          className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-4"
          role="alert"
        >
          <span className="block sm:inline">{error}</span>
        </div>
      )}

      {/* Career Insights */}
      {careerData && (
        <div className="max-w-4xl mx-auto space-y-6">
          {/* Predicted Career */}
          <PredictedCareer career={careerData.prediction} />

          {/* Debug info */}
          {process.env.NODE_ENV === "development" && (
            <pre className="bg-gray-800 text-white p-4 rounded-lg overflow-auto">
              {JSON.stringify(careerData, null, 2)}
            </pre>
          )}

          {/* Career Roadmap */}
          {careerData.insights && (
            <CareerRoadmap insights={careerData.insights} />
          )}

          {/* Salary Insights */}
          {careerData.insights && (
            <SalaryInsights insights={careerData.insights} />
          )}

          {/* Job Insights */}
          {careerData.insights && (
            <JobInsights insights={careerData.insights} />
          )}
        </div>
      )}
    </div>
  );
}

export default App;
